function OpenWebinWeb(web, givenWidth, givenHeight, id){
    document.body.style.margin="0";
    document.body.style.padding="0";
    var huamanWebinWeb = document.createElement("Iframe");
    var att = document.createAttribute("src");    
    att.value = web;  // Set the value
    if(givenWidth === "" || givenWidth === null || givenHeight === null || givenHeight === ""){
        var att1 = document.createAttribute("width");   
        att1.value = window.innerWidth+"px";  
        var att2 = document.createAttribute("height");    
        att2.value = window.innerHeight+"px";  
    }else{
        var att1 = document.createAttribute("width");   
        att1.value = givenWidth+"px";  
        var att2 = document.createAttribute("height");    
        att2.value = givenHeight+"px";  
    }
    if(id !== "" || id !== null){
        var iframeId = document.createAttribute("id");   
        iframeId.value = id;
    }
    var att3 = document.createAttribute("frameborder");    
    att3.value = "0";  
    var att4 = document.createAttribute("id");
    att4.value = "huamanWeb";  
    huamanWebinWeb.setAttributeNode(att); 
    huamanWebinWeb.setAttributeNode(att1);
    huamanWebinWeb.setAttributeNode(att2);
    huamanWebinWeb.setAttributeNode(att3);
    huamanWebinWeb.setAttributeNode(att4);
    document.body.appendChild(huamanWebinWeb);
    document.addEventListener("resize", function(){
            document.body.style.margin="0";
            document.body.style.padding="0";
            var huamanWebinWeb = document.createElement("Iframe");
            var att = document.createAttribute("src");
            att.value = web;
            var att1 = document.createAttribute("width");
            att1.value = window.innerHeight+"px";  
            var att2 = document.createAttribute("height");
            att2.value = window.innerHeight+"px";  
            var att3 = document.createAttribute("frameborder");
            att3.value = "0";  
            var att4 = document.createAttribute("id");
            att4.value = "huamanWeb";  
            huamanWebinWeb.setAttributeNode(att); 
            huamanWebinWeb.setAttributeNode(att1);
            huamanWebinWeb.setAttributeNode(att2);
            huamanWebinWeb.setAttributeNode(att3);
            huamanWebinWeb.setAttributeNode(att4);
            document.body.appendChild(huamanWebinWeb);
    },50);
}
